example.code:
using System;
using Microsoft.Office.Excel.Server.Udf;

namespace MyExcelServicesUdf
{
    <span class="label">[UdfClass]</span>
    public class MyUdfs
    {
        Random rand = new Random();

        [UdfMethod(IsVolatile=true)]
        public int GetRandomNumber()
        {
            return (rand.Next(0, 1000));
        }
    }
}
